import axios from "axios";
import React, { useEffect, useState } from "react";

export default function ManageUser() {
  const [userList, setUserList] = useState([]);
  useEffect(() => {
    axios.get("/user/list").then((res) => setUserList(res.data));
  }, []);
  return (
    <div className="flex justify-center m-5">
      <div className="w-1/2 bg-gray rounded-b-xl p-4">
        <h1 className="font-bold">User List</h1>
        {userList.map((user) => {
          return (
            <div className="flex justify-between my-5">
              <img src="/icon/user.png" alt="user" width={30} />
              <h1>{user._name}</h1>
              <button
                className="w-full px-4 py-2 mt-3 font-bold text-white bg-red rounded-full"
                type="submit"
              >
                Add Category
              </button>
            </div>
          );
        })}
      </div>
    </div>
  );
}

export function AddUser() {}
