import axios from "axios";
import React, { useEffect, useState } from "react";

export default function ManageUser() {
  const [userList, setUserList] = useState([]);

  useEffect(() => {
    axios.get("/user/list").then((res) => setUserList(res.data));
  }, []);
  return (
    <div className="flex justify-center m-5">
      <div className="w-1/2 bg-gray rounded-b-xl p-4">
        <h1 className="font-bold">User List</h1>
        {userList.map((user) => {
          return (
            user._role === "customer" && (
              <div className="flex my-5 items-center justify-between bg-darkgray bg-opacity-25 p-4 rounded-lg">
                <div className="flex items-center">
                  <img src="/icon/user.png" alt="user" width={30} />
                  <h1 className="mx-5">{user._name}</h1>
                </div>
                <div>
                  <button
                    className="px-3 py-1 font-bold text-white bg-red rounded-full"
                    type="submit"
                  >
                    Remove
                  </button>
                </div>
              </div>
            )
          );
        })}
        <button
          className="w-full px-3 py-1 font-bold text-white bg-red rounded-lg"
          type="submit"
        >
          Add new user
        </button>
      </div>
    </div>
  );
}

export function AddUser(props) {
  return (
    <div
      className="fixed z-10 h-1/2 w-1/2 bg-white top-1/2 left-1/2 p-5 rounded-xl"
      style={{ transform: "translate(-50%,-50%)" }}
    >
      <h1 className="font-bold">Add New User</h1>
      <div className="w-full border border-gray rounded-lg"></div>
    </div>
  );
}
