import React, { useEffect, useState, useContext } from "react";
import FilterBar from "../components/Filter";
import axios from "axios";
import AuthContext from "../context/auth";

export default function Profile() {
  const { cookies, removeCookies } = useContext(AuthContext);
  const [user, setUser] = useState({});

  const handleLogOut = () => {
    removeCookies("token");
    window.location.replace("/");
  };

  useEffect(() => {
    axios
      .get("/user/current", { params: { name: cookies.token } })
      .then((res) => setUser(res.data))
      .catch((err) => console.log(err.message));
  }, [cookies]);
  return (
    <div className="font-kanit">
      <FilterBar title="โปรไฟล์" />
      <div className="flex w-full justify-center my-9">
        <div className="flex flex-col w-1/3 bg-gray rounded-xl">
          <div className="flex items-center w-full bg-red rounded-b-xl p-4">
            <img src="/icon/user.png" alt="user" width={50} />
            <p className="font-bold mx-4">{user._name}</p>
          </div>
          <div className="p-4">
            <h1>Email</h1>
            <p className="text-red font-bold">{user._accounts._email}</p>
            <h1>Phone</h1>
            <p className="text-red font-bold">{user._phone}</p>
            <h1>Address</h1>
            <p className="text-red font-bold">{user.address}</p>
            <h1>Birthday</h1>
            <p className="text-red font-bold">{user._birthday}</p>
          </div>
        </div>
      </div>
      <div className="flex justify-center">
        <button className="btn btn-outline-primary" onClick={handleLogOut}>
          ออกจากระบบ
        </button>
      </div>
    </div>
  );
}
