import React, { useState } from "react";

export default function Payment(props) {
  const [cash, setCash] = useState(false);
  const [credit, setCredit] = useState(false);

  const selectCash = () => {
    setCash(true);
    setCredit(false);
  };

  const selectCredit = () => {
    setCredit(true);
    setCash(false);
  };
  return (
    <div
      className="fixed z-10 w-1/2 bg-gray text-black top-1/2 left-1/2 p-5 rounded-xl shadow-xl"
      style={{ transform: "translate(-50%,-50%)" }}
    >
      <div className="flex justify-between w-1/3">
        <h1 className="">Transaction ID</h1>
        <p>0</p>
      </div>
      <div className="flex justify-between w-1/4">
        <h1>Cost</h1>
        <p>178.-</p>
      </div>
      <div className="flex justify-between w-1/4">
        <h1>Shipping Cost</h1>
        <p>50.-</p>
      </div>
      <div className="flex justify-between w-1/4 font-bold">
        <h1>Total Cost</h1>
        <p>223.-</p>
      </div>
      <h1 className="mt-4 font-bold text-2xl">Choose Yout Method</h1>
      <div className="flex">
        <button
          className={`px-4 py-2 mt-3 mr-4 font-bold text-black rounded-lg border-2 ${
            cash && "bg-green"
          }`}
          onClick={() => selectCash()}
        >
          Cash
        </button>
        <button
          className={`px-4 py-2 mt-3 mr-4 font-bold text-black rounded-lg border-2 ${
            credit && "bg-green"
          }`}
          onClick={() => selectCredit()}
        >
          Credit Card
        </button>
      </div>
      {cash && (
        <div className="w-full">
          <button className="w-full px-4 py-2 mt-3 mr-4 font-bold text-white text-xl rounded-xl bg-green">
            <p className="drop-shadow-lg">SUBMIT PAYMENT</p>
          </button>
        </div>
      )}
      {credit && (
        <form className="">
          <div className="flex justify-between">
            <input
              className="px-5 py-2 rounded-lg border w-[48%] my-3"
              type="text"
              placeholder="First Name"
            />
            <input
              className="px-5 py-2 rounded-lg border w-[48%] my-3"
              type="text"
              placeholder="Last Name"
            />
          </div>
          <div className="flex">
            <input
              className="px-5 py-2 rounded-lg border w-1/2 my-3 mr-3"
              type="text"
              placeholder="Card Number"
            />
            <input
              className="px-5 py-2 rounded-lg border w-1/6 my-3 mr-3"
              type="text"
              placeholder="CVV"
            />
            <input
              className="px-5 py-2 rounded-lg border my-3 mr-3"
              type="text"
              placeholder="Expiration date 19-05-2028"
            />
          </div>
          <button className="w-full px-4 py-2 mt-3 mr-4 font-bold text-white text-xl rounded-xl bg-green">
            <p className="drop-shadow-lg">SUBMIT PAYMENT</p>
          </button>
        </form>
      )}
    </div>
  );
}
